status_1="CANCELLED"
status_2="COLLECTED"
status_3="DELIVERED"
status_4="NOT_FOUND"
status_5="BAKING"
status_6="ORDER"
status_7="PREP"
status_8="QUALITY_CONTROL"
status_9="DELIVERY"
status_10="READY_FOR_COLLECTION"

printf "\033c"

while true
do
  json=$(curl --silent "https://www.dominos.co.uk/pizzaTracker/getOrderDetails?id=$TRACKING_ID&noCache=1464800074779" -H 'User-Agent: Mozilla/5.0 (Macintosh; Intel Mac OS X 10_11_4) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/50.0.2661.102 Safari/537.36')

  statusId=$(echo "$json" | grep statusId | cut -d ':' -f 2 | tr -d ' ' | cut -d ',' -f 1)

  lookup="status_$statusId"
  status=$(echo "${!lookup}")

  toilet_message=$(echo "$status" | tr '[:upper:]' '[:lower:]')

  if [[ $statusId == $lastStatus ]]
  then
    printf "."
  else
    echo ""
    echo "${!toilet_message}"
    if [[ $status == "DELIVERY" ]]
    then
      echo "$json" | grep driverName | cut -d ':' -f 2 | tr -d ' ' | cut -d ',' -f 1 | tr -d '"'
    fi
    date +"%T"
  fi
  lastStatus=$statusId
  sleep 10
done
